    function setDistrict(value) {
        $("select#institute_district_id").val(value);
    }

    function setUpazila(value) {
        $("select#institute_upazila_id").val(value);
    }

    function loadDistricts() {
        var selectedDivisionId = $("select#institute_division_id").children("option:selected").val();

        var filteredDistricts = districts.filter(function(i) {
            return i.division_id == selectedDivisionId;
        });

        var upazilasOptions = $('#institute_upazila_id');
        upazilasOptions.empty();
        upazilasOptions.append("<option value='0'>Please Select</option>");

        var districtsOptions = $('#institute_district_id');
        districtsOptions.empty();
        districtsOptions.append("<option value='0'>Please Select</option>");

        for (var i = 0; i < filteredDistricts.length; ++i) {
            districtsOptions.append("<option value='" + filteredDistricts[i].id + "'>" + filteredDistricts[i].name + "</option>");
        }
    }

    function loadUpazilas() {
        var selectedDistrictId = $("select#institute_district_id").children("option:selected").val();

        var filteredUpazilas = upazilas.filter(function(i) {
            return i.district_id == selectedDistrictId;
        });

        var upazilasOptions = $('#institute_upazila_id');
        upazilasOptions.empty();
        upazilasOptions.append("<option value='0'>Please Select</option>");

        for (var i = 0; i < filteredUpazilas.length; ++i) {
            upazilasOptions.append("<option value='" + filteredUpazilas[i].id + "'>" + filteredUpazilas[i].name + "</option>");
        }
    }

    function setCategoryName() {
        var institute_class = $("select#class").children("option:selected").val();

        var primary_options = ["Class 3", "Class 4", "Class 5"];
        var junior_options = ["Class 6", "Class 7", "Class 8"];
        var secondary_options = ["Class 9", "Class 10", "SSC"];
        var hSecondary_options = ["Class 11", "Class 12", "HSC"];

        if (primary_options.includes(institute_class)) {
            $('#category_name').val('Primary');
        } else if (junior_options.includes(institute_class)) {
            $('#category_name').val('Junior');
        } else if (secondary_options.includes(institute_class)) {
            $('#category_name').val('Secondary');
        } else if (hSecondary_options.includes(institute_class)) {
            $('#category_name').val('Higher Secondary');
        }
    }
